// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.5.0;

contract VotingSystem {
  uint public candidateCount=0;

  struct Candidate{
    uint id;
    string name;
    string party;
    uint vote;
  }

  mapping(uint => Candidate) public candidates;
  constructor() public { 
    addCandidate("Usha Nandhini","NMK");
    addCandidate("Saba Vaissh","VMK");

  }
  function addCandidate(string memory name, string memory partyName)public{
    candidateCount ++;
    candidates[candidateCount] = Candidate(candidateCount,name,partyName,0);
  }
  function vote(uint candidateId) public {
require(candidateId > 0 && candidateId <= candidateCount, "Invalid candidate ID.");
candidates[candidateId].vote++;
}

function unvote(uint candidateId) public {
require(candidateId > 0 && candidateId <= candidateCount, "Invalid candidate ID.");
require(candidates[candidateId].vote > 0, "Candidate has no votes to unvote.");
candidates[candidateId].vote--;
}
}
